    async function DepartmentChange(event,name,elementToSearchIn){

        if(event.target.classList.contains('active')){
            return;
        }
        const containerDiv =  document.getElementById(elementToSearchIn);
        const links = containerDiv.querySelector('.department-links').childNodes;
        links.forEach(element => {
            if(element.tagName == 'A'){
                element.classList.remove('active')
            }
        });
        event.target.classList.add('active');
        var imageDepartmant = containerDiv.querySelector('#imageDepartmant');
        var TitleDepartmant = containerDiv.querySelector('#TitleDepartmant');
        var SubTitleDepartment = containerDiv.querySelector('#SubTitleDepartment');
        var descDepartment = containerDiv.querySelector('#descDepartment');
        var blogPage = containerDiv.querySelector('#blog-page');

        imageDepartmant.src = CateogryBlogs[name]['slid_img'];
        TitleDepartmant.innerHTML = CateogryBlogs[name]['slid_title'];
        SubTitleDepartment.innerHTML = CateogryBlogs[name]['title'];
        descDepartment.innerHTML = getSimpleText(CateogryBlogs[name]['desc1']);
        blogPage.href="/blog/"+name;
    }
    function getSimpleText(text) {
            // Remove all <br> tags
            const textWithoutBr = text.replace(/<br\s*\/?>/gi, ' ');

            // Get the first 500 characters
            const trimmedText = textWithoutBr.substring(0, 500);

            // Return the trimmed text followed by an ellipsis
            return trimmedText + '...';
    }

  </script>
  @endsection




